// Mobile
// Home > Products
var interactiveSlideProducts = document.getElementsByClassName("interactive-slide-providers")[0];
var interactiveButtonProducts = document.getElementsByClassName("interactive-button-products")[0];
var interactiveSlideModallogin = document.getElementsByClassName("interactive-slide-modallogin")[0];

if (interactiveSlideProducts !== null) {
  gsap.set(interactiveSlideProducts, {
    display: "none",
  });

  var showProviders = false;

  interactiveButtonProducts.addEventListener("click", () => {
    showProviders = !showProviders;

    if (showProviders === true) {
      gsap.set(interactiveButtonProducts, {
        className: "+=button-navbar -active",
      });

      gsap.to(interactiveSlideModallogin, {
        duration: 0.25,
        y: "-3rem",

        onComplete: function () {
          gsap.to(interactiveSlideProducts, {
            duration: 0.25,
            display: "flex",
            autoAlpha: 1,
          });
        },
      });
    } else {
      gsap.set(interactiveButtonProducts, {
        className: "-=button-navbar",
      });

      gsap.to(interactiveSlideProducts, {
        duration: 0.25,
        display: "none",
        autoAlpha: 0,
        onComplete: function () {
          gsap.to(interactiveSlideModallogin, {
            duration: 0.25,
            y: 0,
          });
        },
      });
    }
  });
}

