// Set Modal lanuages
$(".interactive-lanuages-switch").on("click", function () {
  gsap.to(".interactive-lanuages-modal", {
    duration: 0.25,
    autoAlpha: 1,
  });
});

// The default language is Thai language
var language = "th-th";

// Check for localStorage support
if ("localStorage" in window) {
  var usrLanguage = localStorage.getItem("uiLanguage");

  if (usrLanguage) {
    language = usrLanguage;
  }

  if (language === "th-th") {
    $(".interactive-lanuages-switch").css("background-image", 'url("/src/assets/icons/flag/th.webp")');

    gsap.set(".interactive-translate-lanuage.-en", {
      display: "block",
      autoAlpha: 1,
    });

    gsap.set(".interactive-translate-lanuage.-th", {
      display: "none",
      autoAlpha: 0,
    });
  } else if (language === "en-gb") {
    $(".interactive-lanuages-switch").css("background-image", 'url("/src/assets/icons/flag/en.webp")');

    gsap.set(".interactive-translate-lanuage.-en", {
      display: "none",
      autoAlpha: 0,
    });

    gsap.set(".interactive-translate-lanuage.-th", {
      display: "block",
      autoAlpha: 1,
    });
  }
}

$(document).ready(function () {
  $(".ml-language").each(function (index, element) {
    $(this).text(arrLang[language][$(this).data("language")]);
  });

  $(".input-language").each(function (index, element) {
    $(this).attr("placeholder", arrLang[language][$(this).data("language")]);
  });
});

// get or set the selected language
$(".interactive-translate-lanuage").on("click", function () {
  var language = $(this).attr("id");

  // Update localStorage key
  if ("localStorage" in window) {
    localStorage.setItem("uiLanguage", language);
  }

  $(".ml-language").each(function (index, element) {
    $(this).text(arrLang[language][$(this).data("language")]);
  });

  $(".input-language").each(function (index, element) {
    $(this).attr("placeholder", arrLang[language][$(this).data("language")]);
  });

  // Update Flag
  if (language === "th-th") {
    $(".interactive-lanuages-switch").css("background-image", 'url("/src/assets/icons/flag/th.webp")');

    gsap.set(".interactive-translate-lanuage.-en", {
      display: "block",
      autoAlpha: 1,
    });

    gsap.set(".interactive-translate-lanuage.-th", {
      display: "none",
      autoAlpha: 0,
    });
  } else if (language === "en-gb") {
    $(".interactive-lanuages-switch").css("background-image", 'url("/src/assets/icons/flag/en.webp")');

    gsap.set(".interactive-translate-lanuage.-en", {
      display: "none",
      autoAlpha: 0,
    });

    gsap.set(".interactive-translate-lanuage.-th", {
      display: "block",
      autoAlpha: 1,
    });
  }

  gsap.to(".interactive-lanuages-modal", {
    duration: 0.25,
    autoAlpha: 0,
  });
});
